mapper.insert:
INSERT INTO ${table}
(username, host, configkey, subkey, configinfo, version, operate_plat)
VALUES (#{username}, #{host}, #{key}, #{subkey}, #{value}, #{version}, #{operate_plat});

INSERT INTO ${table} (username, host, configkey, subkey, configinfo, version, operate_plat, update_time) VALUES
(#{username}, #{host}, #{item.key}, #{item.subkey}, #{item.value}, #{version}, #{operate_plat}, now() + '${index}S')
ON CONFLICT (username, host, configkey, subkey) DO UPDATE SET version = #{version}, isdel = 0, configinfo = EXCLUDED.configinfo, operate_plat = EXCLUDED.operate_plat;

mapper.update:
UPDATE ${table}
SET version  = #{version},
isdel = #{isdel},
update_time = now(),
operate_plat = #{operate_plat}
WHERE
username = #{username} and
host = #{host} and
(configkey, subkey) IN
(
(#{data.key},#{data.subkey})
 )
;
